mapper.update:
update community 
set com_readcount = com_readcount + 1 
where com_num = #{com_num}

update community  
set com_reply_seq = com_reply_seq + 1 
where com_ref = #{com_ref} 
and com_lev = #{com_lev}

update community  
set com_reply_seq = com_reply_seq + 1 
where com_ref = #{com_ref} 
and com_lev = #{com_lev} 
and com_reply_ref = 	#{com_reply_ref}

mapper.delete:
delete from community  

	where com_ref = #{com_num}


	where com_reply_ref = #{com_num}


	where com_num = #{com_num}

mapper.update:
update community  
set com_content = #{com_content}, 
where com_num = #{com_num}

update community set com_content = #{com_content},
com_original_filename = #{com_original_filename}, com_rename_filename = #{com_rename_filename}
, com_original_filename = null, com_rename_filename = null
where com_num = #{com_num}
