mapper.select:
select * from camera
where user_id = #{ userid }
order by camera_num desc

mapper.update:
update product
set item = #{item}, quantity = #{quantity}, unit_price = #{unit_price}, price = #{price}
, original_filepath = #{original_filepath}, rename_filepath = #{rename_filepath}
, original_filepath = null, rename_filepath = null
where camera_num = #{camera_num} and product_num =#{ product_num }

update product

set item = #{item}, 
	quantity = #{quantity},
	unit_price = #{unit_price},
	price = #{price}

	where camera_num = #{camera_num} and product_num =#{ product_num }

mapper.delete:
{call
	declare
	begin
delete from product where camera_num = #{camera_num};
delete from camera where camera_num = #{camera_num};
end
}
